import React from 'react';
import './App.css';
import { Amplify } from 'aws-amplify';
import { Authenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import awsExports from './aws-exports';

// Configura o Amplify com as definições do aws-exports.js
Amplify.configure(awsExports);

function App() {

  const handleAuthStateChange = (authState) => {
    if (authState === 'signedIn') {
      // Redireciona o utilizador para o site deployed após o login
      window.location.href = 'http://13.51.235.59'; // Substitua pelo link correto do seu site
    }
  };

  return (
    <div className="App">
      <Authenticator onStateChange={handleAuthStateChange}>
        {({ signOut }) => (
          <main>
            <header className='App-header'>
              <button 
                onClick={signOut} 
                style={{ 
                  margin: '20px', 
                  fontSize: '0.8rem', 
                  padding: '5px 10px', 
                  marginTop: '20px'
                }}
              >
                Sign Out
              </button>
            </header>
          </main>
        )}
      </Authenticator>
    </div>
  );
}

export default App;
