import React, { useEffect } from 'react';
import './App.css';
import { Amplify, Auth } from 'aws-amplify';
import { Authenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import awsExports from './aws-exports';

// Configura o Amplify com as definições do aws-exports.js
Amplify.configure(awsExports);

function App() {
  useEffect(() => {
    // Verificar o estado de autenticação ao carregar a página
    Auth.currentAuthenticatedUser()
      .then(user => {
        // Se o usuário está autenticado, redireciona para o seu site deployed
        window.location.href = 'http://13.51.235.59'; // Substitua pelo link do seu site
      })
      .catch(() => {
        // Se não está autenticado, o usuário verá a tela de login
        console.log('Usuário não autenticado');
      });
  }, []);

  return (
    <div className="App">
      <Authenticator>
        {({ signOut }) => (
          <main>
            <header className="App-header">
              <button
                onClick={signOut}
                style={{
                  margin: '20px',
                  fontSize: '0.8rem',
                  padding: '5px 10px',
                  marginTop: '20px',
                }}
              >
                Sign Out
              </button>
            </header>
          </main>
        )}
      </Authenticator>
    </div>
  );
}

export default App;
